mapper.select:
select

from t_user


  user_name = #{userName,jdbcType=VARCHAR}


  and pass_word = #{passWord,jdbcType=VARCHAR}


  and age = #{age,jdbcType=INTEGER}


  and user_num = #{userNum,jdbcType=VARCHAR}


  and sex = #{sex,jdbcType=VARCHAR}


  and department = #{department,jdbcType=VARCHAR}


  and  user_class = #{userClass,jdbcType=VARCHAR}


  and professional = #{professional,jdbcType=VARCHAR}


  and birthday = #{birthday,jdbcType=VARCHAR}

  and is_delete=0

mapper.update:
update t_user
set is_delete =1
where user_id = #{userId,jdbcType=VARCHAR}
